import React, { useState } from 'react';
import "./formulario.css";
import CampoInput from "../campo-inputs";
import CampoTextarea from "../campo-textarea";
import BotonAzul from "../boton-azul";
import BotonBlanco from "../boton-blanco";
import Categorias from "../categorias";
import { useNavigate } from 'react-router-dom';


const NuevoVideo = (props) => {

    const [video, setVideo] = useState({
        titulo: '',
        link: '',
        imagen: '',
        descripcion: '',
        codigoSeguridad: '',
        categoria: '',
    });

    const limpiarFormulario = () => {
        setVideo({
            titulo: '',
            link: '',
            imagen: '',
            descripcion: '',
            codigoSeguridad: '',
            categoria: '',
        });
    };

    const navigate = useNavigate();

    const irNuevaCategoria = () => {
        navigate('/nuevaCategoria');
    };

    const manejarEnvio = (e) => {
        e.preventDefault();
        console.log("Manejar el envio")
        let datosAEnviar = {
            titulo: nombre,
            link: link,
            imagen: imagen,
            descripcion: descripcion,
            codigoSeguridad: codigoSeguridad,
            categoria: categoria
        }
        console.log(datosAEnviar)
    }

    return (
        <section className="formulario-nuevo">
            <h2>Nuevo video</h2>
            <form onSubmit={manejarEnvio}>
                <CampoInput
                    title="Título:"
                    value={video.titulo}
                    actualizarValor={setVideo.titulo}
                    required
                ></CampoInput>

                <CampoInput
                    title="Link del vídeo:"
                    value={video.link}
                    actualizarValor={setVideo.link}
                    required
                ></CampoInput>

                <CampoInput
                    title="Link imagen del vídeo:"
                    value={video.imagen}
                    actualizarValor={setVideo.imagen}
                    required
                ></CampoInput>

                <CampoTextarea
                    title="Descripción:"
                    value={video.descripcion}
                    actualizarValor={setVideo.descripcion}
                    required
                ></CampoTextarea>

                <CampoInput
                    title="Código de seguridad:"
                    value={video.codigoSeguridad}
                    actualizarValor={setVideo.codigoSeguridad}
                    required
                ></CampoInput>

                <Categorias
                    title="Categoría:"
                    value={video.categoria}
                    actualizarValor={setVideo.categoria}
                ></Categorias>

                <div className="botones">
                    <div className="botones-add">
                        <BotonAzul id="ck" title="Guardar"></BotonAzul>
                        <BotonBlanco title="Limpiar" onClick={limpiarFormulario}></BotonBlanco>
                    </div>
                    <BotonAzul title="Nueva categoría" onClick={irNuevaCategoria} />
                </div>
            </form>
        </section>
    );
}

export default (NuevoVideo);
