import React, { useState } from 'react';
import './banner.css';
import VideoImg from '../video';

const Banner = (props) => {
  const { nuevosVideos } = props;
  const ultimoVideo = nuevosVideos[nuevosVideos.length - 1];

  return (
    <div className="banner-elementos">
      <div className="banner-elementos-texto">
        <h2 className="banner-titulo-principal">{props.categoria}</h2>
        <h3 className="banner-titulo-secundario">Challenge React</h3>
        <p className="banner-descripcion">
          Este challenge es una forma de aprendizaje. Es un mecanismo donde podrás comprometerte en la resolución de un problema para poder aplicar todos los conocimientos adquiridos en la formación React.
        </p>
      </div>
      {ultimoVideo && (
        <VideoImg
          src={ultimoVideo.imagen}
          alt={ultimoVideo.titulo}
          title={ultimoVideo.titulo}
          href={ultimoVideo.link}
          key={ultimoVideo.codigoSeguridad}
          categoria={ultimoVideo.categoria}
          color={props.datos.color}
        />
      )}
    </div>
  );
};

export default Banner;
