import React, { useState } from 'react';
import "./formulario.css";
import CampoInput from "../campo-inputs";
import CampoTextarea from "../campo-textarea";
import BotonAzul from "../boton-azul";
import BotonBlanco from "../boton-blanco";
import Categorias from "../categorias";
import { useNavigate } from 'react-router-dom';

const NuevoVideo = (props) => {

    const [video, setVideo] = useState({
        titulo: '',
        link: '',
        imagen: '',
        descripcion: '',
        codigoSeguridad: '',
        categoria: '',
    });

    const limpiarFormulario = () => {
        setVideo({
            titulo: '',
            link: '',
            imagen: '',
            descripcion: '',
            codigoSeguridad: '',
            categoria: '',
        });
    };

    const navigate = useNavigate();

    const irNuevaCategoria = () => {
        navigate('/nuevaCategoria');
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Aquí puedes realizar las acciones necesarias con los datos del formulario
        // Por ejemplo, puedes enviar los datos a un servidor, realizar validaciones, etc.
        console.log(video);
        limpiarFormulario();
    };

    return (
        <section className="formulario-nuevo">
            <h2>Nuevo video</h2>
            <form onSubmit={handleSubmit}>
                <CampoInput
                    title="Título:"
                    value={video.titulo}
                    onChange={(e) => setVideo({ ...video, titulo: e.target.value })}
                    required
                ></CampoInput>

                <CampoInput
                    title="Link del vídeo:"
                    value={video.link}
                    onChange={(e) => setVideo({ ...video, link: e.target.value })}
                    required
                ></CampoInput>

                <CampoInput
                    title="Link imagen del vídeo:"
                    value={video.imagen}
                    onChange={(e) => setVideo({ ...video, imagen: e.target.value })}
                    required
                ></CampoInput>

                <CampoTextarea
                    title="Descripción:"
                    value={video.descripcion}
                    onChange={(e) => setVideo({ ...video, descripcion: e.target.value })}
                    required
                ></CampoTextarea>

                <CampoInput
                    title="Código de seguridad:"
                    value={video.codigoSeguridad}
                    onChange={(e) => setVideo({ ...video, codigoSeguridad: e.target.value })}
                    required
                ></CampoInput>

                <Categorias
                    title="Categoría:"
                    value={video.categoria}
                    onChange={(e) => setVideo({ ...video, categoria: e.target.value })}
                ></Categorias>

                <div className="botones">
                    <div className="botones-add">
                        <BotonAzul id="ck" type="submit" title="Guardar"></BotonAzul>
                        <BotonBlanco title="Limpiar" onClick={limpiarFormulario}></BotonBlanco>
                    </div>
                    <BotonAzul title="Nueva categoría" onClick={irNuevaCategoria} />
                </div>
            </form>
        </section>
    );
}

export default NuevoVideo;
