import React from 'react';
import '../grupo/grupo.css';
import VideoImg from '../video';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Grupo = (props) => {

  const PantallaCelular = window.innerWidth <= 480;
  const PantallaTablet = window.innerWidth <=768;
  const PantallaPC = window.innerWidth <=1000;

  const settings = {
    dots: true,
    infinite: props.nuevosVideos.length > 1,
    speed: 500,
    slidesToShow: PantallaCelular ? 1 : PantallaTablet ? 2 : 3,
    slidesToScroll: 2,
    vertical: false,
    centerMode: false,
    centerPadding: '45px'
  };

  console.log(props.nuevosVideos.length > 0);

  return (
    props.nuevosVideos.length > 0 && (
      <section className="grupo">
        <div className="grupo-detalles">
          <h3 style={{ backgroundColor: props.datos.color }}>{props.datos.titulo}</h3>
          <p>Formaci√≥n {props.datos.titulo} de Alura Latam</p>
        </div>
        <div className="grupo-videos">
          <Slider {...settings}>
            {props.nuevosVideos.map((video) => (
              <VideoImg
                src={video.imagen}
                alt={video.titulo}
                title={video.titulo}
                href={video.link}
                key={video.codigoSeguridad}
                categoria={video.categoria}
                color={props.datos.color}
              />
            ))}
          </Slider>
        </div>
      </section>
    )
  );
};

export default Grupo;
