import React from 'react';
import './grupo.css';
import VideoImg from '../video';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';


const Grupo = (props) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        vertical: false,
        centerMode: true,
        centerPadding: '45px'
    };

    return (
        <section className="grupo">
            <div className="grupo-detalles">
                <h3 style={{ backgroundColor: props.datos.color }}>{props.datos.titulo}</h3>
                <p>Formaci√≥n {props.datos.titulo} de Alura Latam</p>
            </div>
            <div className="grupo-videos">
                <Slider {...settings}>
                    <VideoImg
                        src="https://i.ytimg.com/an_webp/kyFiT4ofMwk/mqdefault_6s.webp?du=3000&sqp=CIfAyqMG&rs=AOn4CLB_YPPaXXwa2rL7Ag8QQo8_HWHhKg"
                        alt="logo"
                        style={{ borderColor: "blue" }}
                        href="https://www.youtube.com/watch?v=hs1jegIQzmg&ab_channel=HER%CE%98SWOR%C3%90"
                        title="Video 1"
                        />
                    
                    <VideoImg
                        src="https://i.ytimg.com/vi/hs1jegIQzmg/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLC34OEXYDxfi52FFUX94EhNq0LkuQ"
                        alt="logo"
                        style={{ borderColor: "blue" }}
                        href="https://www.youtube.com/watch?v=hs1jegIQzmg&ab_channel=HER%CE%98SWOR%C3%90" 
                        title="Video 2"
                        />

                    <VideoImg
                        src="https://i.ytimg.com/an_webp/jZLh6STDrMc/mqdefault_6s.webp?du=3000&sqp=CNuIyqMG&rs=AOn4CLAaBWqqUCkeCcL0d8KLJS8mHdKHAw"
                        alt="logo"
                        style={{ borderColor: "blue" }}
                        href="https://www.youtube.com/watch?v=hs1jegIQzmg&ab_channel=HER%CE%98SWOR%C3%90" 
                        title="Video 3"
                        />
                </Slider>
            </div>
        </section>
    );
};

export default Grupo
